add_action( 'wp_ajax_get_trails_by_region', 'get_trails_by_region' );
add_action( 'wp_ajax_nopriv_get_trails_by_region', 'get_trails_by_region' );

function get_trails_by_region(){

    switch ($_POST['region']){
        case 'scotland':
            $region = 'scotland-trails';
            break;
        case 'n-east':
            $region = 'north-east-trails';
            break;
        case 'n-west':
            $region = 'north-west-trails';
            break;
        case 'yorkshire':
            $region = 'yorkshire-trails';
            break;
        case 'w-midlands':
            $region = 'west-midlands-trails';
            break;
        case 'e-midlands':
            $region = 'east-midlands-trails';
            break;
        case 'wales':
            $region = 'wales-trails';
            break;
        case 'e-london':
            $region = 'east-of-london';
            break;
        case 'london':
            $region = 'greater-london';
            break;
        case 's-east':
            $region = 'south-east';
            break;
        case 's-west':
            $region = 'south-west-trails';
            break;
        case 'n-ireland':
            $region = 'northern-ireland';
            break;
        case 'ireland':
            $region = 'ireland-trails';
            break;
    }

    $term = get_term_by('slug' , $region , 'product_cat');

    echo json_encode(array('region' => $term->name , 'colour' => $_POST['color']));

    die();

}